    private fun getBestImageUrlFromApi(item: com.example.natraj.data.wp.WpMediaItem): String {
        val sizes = item.mediaDetails?.sizes
        if (sizes != null) {
            val preferredSizes = listOf("large", "full", "medium_large", "medium")
            for (sizeName in preferredSizes) {
                sizes[sizeName]?.let { size ->
                    return size.sourceUrl
                }
            }
        }
        return item.sourceUrl
    }

    // New WordPress API methods
    suspend fun getPages(limit: Int = AppConfig.getBlogPostsLimit(context)): List<WpPage> {
        val pages = api.getPages(perPage = limit)
        return pages.map { p ->
            WpPage(
                id = p.id,
                date = p.date,
                link = p.link,
                title = HtmlCompat.fromHtml(p.title.rendered, HtmlCompat.FROM_HTML_MODE_LEGACY).toString(),
                content = HtmlCompat.fromHtml(p.content.rendered, HtmlCompat.FROM_HTML_MODE_LEGACY).toString(),
                excerpt = HtmlCompat.fromHtml(p.excerpt.rendered, HtmlCompat.FROM_HTML_MODE_LEGACY).toString()
            )
        }
    }

    suspend fun getWpCategories(limit: Int = AppConfig.getBlogPostsLimit(context)): List<WpCategory> {
        val categories = api.getCategories(perPage = limit)
        return categories.map { c ->
            WpCategory(
                id = c.id,
                name = c.name,
                slug = c.slug,
                description = c.description,
                count = c.count
            )
        }
    }

    suspend fun getTags(limit: Int = AppConfig.getBlogPostsLimit(context)): List<WpTag> {
        val tags = api.getTags(perPage = limit)
        return tags.map { t ->
            WpTag(
                id = t.id,
                name = t.name,
                slug = t.slug,
                description = t.description,
                count = t.count
            )
        }
    }

    suspend fun getUsers(limit: Int = AppConfig.getBlogPostsLimit(context)): List<WpUser> {
        val users = api.getUsers(perPage = limit)
        return users.map { u ->
            WpUser(
                id = u.id,
                name = u.name,
                slug = u.slug,
                description = u.description,
                avatarUrls = u.avatarUrls
            )
        }
    }

    suspend fun getAllMedia(limit: Int = AppConfig.getRecentOrdersLimit(context)): List<WpMediaItem> {
        val mediaItems = api.getMedia(perPage = limit, search = null)
        return mediaItems.map { m ->
            WpMediaItem(
                id = m.id,
                title = HtmlCompat.fromHtml(m.title.rendered, HtmlCompat.FROM_HTML_MODE_LEGACY).toString(),
                sourceUrl = m.sourceUrl,
                altText = m.altText,
                caption = m.caption?.rendered?.let {
                    HtmlCompat.fromHtml(it, HtmlCompat.FROM_HTML_MODE_LEGACY).toString()
                },
                mediaDetails = m.mediaDetails
            )
        }
    }
}

// Data classes for WordPress content
data class WpPage(
    val id: Int,
    val date: String,
    val link: String,
    val title: String,
    val content: String,
    val excerpt: String
)

data class WpCategory(
    val id: Int,
    val name: String,
    val slug: String,
    val description: String,
    val count: Int
)

data class WpTag(
    val id: Int,
    val name: String,
    val slug: String,
    val description: String,
    val count: Int
)

data class WpUser(
    val id: Int,
    val name: String,
    val slug: String,
    val description: String,
    val avatarUrls: Map<String, String>?
)

data class WpMediaItem(
    val id: Int,
    val title: String,
    val sourceUrl: String,
    val altText: String?,
    val caption: String?,
    val mediaDetails: WpMediaDetails?
)

package com.example.natraj.data.wp

import android.content.Context
import android.content.SharedPreferences
import com.example.natraj.data.WpRepository

class WpAuthManager(private val context: Context) {

    private val prefs: SharedPreferences = context.getSharedPreferences("wp_auth", Context.MODE_PRIVATE)
    private val wpRepository = WpRepository(context)

    companion object {
        private const val KEY_TOKEN = "jwt_token"
        private const val KEY_USER_EMAIL = "user_email"
        private const val KEY_USER_NAME = "user_name"
        private const val KEY_USER_DISPLAY_NAME = "user_display_name"
        private const val KEY_IS_LOGGED_IN = "is_logged_in"
    }

    suspend fun login(username: String, password: String): Result<WpLoginResponse> {
        return try {
            val request = WpLoginRequest(username, password)
            val response = wpRepository.api.login(request)

            // Save credentials
            prefs.edit().apply {
                putString(KEY_TOKEN, response.token)
                putString(KEY_USER_EMAIL, response.userEmail)
                putString(KEY_USER_NAME, response.userNicename)
                putString(KEY_USER_DISPLAY_NAME, response.userDisplayName)
                putBoolean(KEY_IS_LOGGED_IN, true)
                apply()
            }

            Result.success(response)
        } catch (e: Exception) {
            Result.failure(e)
        }
    }

    suspend fun register(
        username: String,
        email: String,
        password: String,
        firstName: String? = null,
        lastName: String? = null
    ): Result<WpRegisterResponse> {
        return try {
            val request = WpRegisterRequest(username, email, password, firstName, lastName)
            val response = wpRepository.api.register(request)
            Result.success(response)
        } catch (e: Exception) {
            Result.failure(e)
        }
    }

    fun logout() {
        prefs.edit().clear().apply()
    }

    fun isLoggedIn(): Boolean {
        return prefs.getBoolean(KEY_IS_LOGGED_IN, false) && !getToken().isNullOrBlank()
    }

    fun getToken(): String? {
        return prefs.getString(KEY_TOKEN, null)
    }

    fun getUserEmail(): String? {
        return prefs.getString(KEY_USER_EMAIL, null)
    }

    fun getUserName(): String? {
        return prefs.getString(KEY_USER_NAME, null)
    }

    fun getUserDisplayName(): String? {
        return prefs.getString(KEY_USER_DISPLAY_NAME, null)
    }
}

package com.example.natraj.data.wp

import android.content.Context
import android.content.SharedPreferences
import com.example.natraj.data.repository.WpRepository

class WpAuthManager(private val context: Context) {

    private val prefs: SharedPreferences = context.getSharedPreferences("wp_auth", Context.MODE_PRIVATE)
    private val wpRepository = WpRepository(context)

    companion object {
        private const val KEY_TOKEN = "jwt_token"
        private const val KEY_USER_EMAIL = "user_email"
        private const val KEY_USER_NAME = "user_name"
        private const val KEY_USER_DISPLAY_NAME = "user_display_name"
        private const val KEY_IS_LOGGED_IN = "is_logged_in"
    }

    suspend fun login(username: String, password: String): Result<WpLoginResponse> {
        return try {
            val request = WpLoginRequest(username, password)
            val response = wpRepository.api.login(request)

            // Save credentials
            prefs.edit().apply {
                putString(KEY_TOKEN, response.token)
                putString(KEY_USER_EMAIL, response.userEmail)
                putString(KEY_USER_NAME, response.userNicename)
                putString(KEY_USER_DISPLAY_NAME, response.userDisplayName)
                putBoolean(KEY_IS_LOGGED_IN, true)
                apply()
            }

            Result.success(response)
        } catch (e: Exception) {
            Result.failure(e)
        }
    }

    suspend fun register(
        username: String,
        email: String,
        password: String,
        firstName: String? = null,
        lastName: String? = null
    ): Result<WpRegisterResponse> {
        return try {
            val request = WpRegisterRequest(username, email, password, firstName, lastName)
            val response = wpRepository.api.register(request)
            Result.success(response)
        } catch (e: Exception) {
            Result.failure(e)
        }
    }

    fun logout() {
        prefs.edit().clear().apply()
    }

    fun isLoggedIn(): Boolean {
        return prefs.getBoolean(KEY_IS_LOGGED_IN, false) && !getToken().isNullOrBlank()
    }

    fun getToken(): String? {
        return prefs.getString(KEY_TOKEN, null)
    }

    fun getUserEmail(): String? {
        return prefs.getString(KEY_USER_EMAIL, null)
    }

    fun getUserName(): String? {
        return prefs.getString(KEY_USER_NAME, null)
    }

    fun getUserDisplayName(): String? {
        return prefs.getString(KEY_USER_DISPLAY_NAME, null)
    }
}

package com.example.natraj

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageView
import android.widget.TextView
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide

class BannerAdapter(
    private val banners: List<Banner>,
    private val onBannerClick: (Banner) -> Unit = {}
) : RecyclerView.Adapter<BannerAdapter.BannerViewHolder>() {

    inner class BannerViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        private val bannerImage: ImageView = itemView.findViewById(R.id.banner_image)
        private val bannerTitle: TextView = itemView.findViewById(R.id.banner_title)
        private val bannerSubtitle: TextView = itemView.findViewById(R.id.banner_subtitle)
        private val bannerDescription: TextView = itemView.findViewById(R.id.banner_description)

        fun bind(banner: Banner) {
            bannerTitle.text = banner.title
            bannerSubtitle.text = banner.subtitle
            bannerDescription.text = banner.description

            // Load banner image with better quality settings
            if (banner.imageUrl.isNotEmpty()) {
                Glide.with(itemView.context)
                    .load(banner.imageUrl)
                    .diskCacheStrategy(com.bumptech.glide.load.engine.DiskCacheStrategy.ALL)
                    .skipMemoryCache(false)
                    .placeholder(android.R.color.transparent)
                    .error(android.R.color.transparent)
                    .centerCrop()
                    .into(bannerImage)
            } else {
                // If no image URL, hide the image view to show gradient background
                bannerImage.setImageDrawable(null)
            }

            itemView.setOnClickListener { onBannerClick(banner) }
        }
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): BannerViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.item_banner, parent, false)
        return BannerViewHolder(view)
    }

    override fun onBindViewHolder(holder: BannerViewHolder, position: Int) {
        holder.bind(banners[position])
    }

    override fun getItemCount(): Int = banners.size
}

package com.example.natraj

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageView
import android.widget.TextView
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide

class BannerAdapter(
    private val banners: List<Banner>,
    private val onBannerClick: (Banner) -> Unit = {}
) : RecyclerView.Adapter<BannerAdapter.BannerViewHolder>() {

    inner class BannerViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        private val bannerImage: ImageView = itemView.findViewById(R.id.banner_image)
        private val bannerTitle: TextView = itemView.findViewById(R.id.banner_title)
        private val bannerSubtitle: TextView = itemView.findViewById(R.id.banner_subtitle)
        private val bannerDescription: TextView = itemView.findViewById(R.id.banner_description)

        fun bind(banner: Banner) {
            bannerTitle.text = banner.title
            bannerSubtitle.text = banner.subtitle
            bannerDescription.text = banner.description

            // Load banner image with better quality settings
            if (banner.imageUrl.isNotEmpty()) {
                val placeholder = try {
                    // use a local drawable if present
                    itemView.context.resources.getIdentifier("ic_banner_placeholder", "drawable", itemView.context.packageName)
                } catch (ex: Exception) { 0 }

                val placeholderRes = if (placeholder != 0) placeholder else android.R.color.transparent

                Glide.with(itemView.context)
                    .load(banner.imageUrl)
                    .diskCacheStrategy(com.bumptech.glide.load.engine.DiskCacheStrategy.ALL)
                    .skipMemoryCache(false)
                    .placeholder(placeholderRes)
                    .error(placeholderRes)
                    .centerCrop()
                    .into(bannerImage)
            } else {
                // If no image URL, clear the image to show gradient background
                bannerImage.setImageDrawable(null)
            }

            itemView.setOnClickListener { onBannerClick(banner) }
        }
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): BannerViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.item_banner, parent, false)

        // Ensure the page fills the whole ViewPager2 (fixes "Pages must fill the whole ViewPager2" crash)
        val lp = view.layoutParams
        if (lp == null) {
            view.layoutParams = RecyclerView.LayoutParams(ViewGroup.LayoutParams.MATCH_PARENT, ViewGroup.LayoutParams.MATCH_PARENT)
        } else {
            lp.width = ViewGroup.LayoutParams.MATCH_PARENT
            lp.height = ViewGroup.LayoutParams.MATCH_PARENT
            view.layoutParams = lp
        }

        return BannerViewHolder(view)
    }

    override fun onBindViewHolder(holder: BannerViewHolder, position: Int) {
        holder.bind(banners[position])
    }

    override fun getItemCount(): Int = banners.size
}

package com.example.natraj.ui.fragments

import android.content.Intent
import android.content.SharedPreferences
import android.net.Uri
import android.os.Bundle
import android.view.LayoutInflater
