package com.example.natraj

import android.content.Context
import android.content.SharedPreferences
import com.example.natraj.data.model.Product
import com.google.gson.Gson
import com.google.gson.reflect.TypeToken

object CartManager {
    private val items = mutableListOf<CartItem>()
    private val listeners = mutableListOf<() -> Unit>()
    private var sharedPrefs: SharedPreferences? = null
    private val gson = Gson()

    fun initialize(context: Context) {
        sharedPrefs = context.getSharedPreferences("cart_prefs", Context.MODE_PRIVATE)
        loadCartFromStorage()
    }

    private fun loadCartFromStorage() {
        sharedPrefs?.let { prefs ->
            val cartJson = prefs.getString("cart_items", "[]")
            try {
                val type = com.google.gson.reflect.TypeToken.getParameterized(java.util.List::class.java, CartItem::class.java).type
                val savedItems: List<CartItem> = gson.fromJson(cartJson, type) ?: emptyList()
                items.clear()
                items.addAll(savedItems)
            } catch (e: Exception) {
                items.clear() // Clear if parsing fails
            }
        }
    }

    private fun saveCartToStorage() {
        sharedPrefs?.let { prefs ->
            try {
                val cartJson = gson.toJson(items)
                prefs.edit().putString("cart_items", cartJson).apply()
            } catch (e: Exception) {
                // Handle save error silently
            }
        }
    }

    fun add(product: Product, qty: Int = 1) {
        val existing = items.find { it.product.id == product.id }
        if (existing != null) {
            existing.quantity += qty
        } else {
            items.add(CartItem(product, qty))
        }
        saveCartToStorage()
        notifyListeners()
    }

    fun remove(product: Product) {
        items.removeAll { it.product.id == product.id }
        saveCartToStorage()
        notifyListeners()
    }

    fun updateQuantity(product: Product, qty: Int) {
        val item = items.find { it.product.id == product.id }
        if (item != null) {
            if (qty <= 0) {
                remove(product)
            } else {
                item.quantity = qty
                saveCartToStorage()
                notifyListeners()
            }
        }
    }

    fun clear() {
        items.clear()
        saveCartToStorage()
        notifyListeners()
    }

    fun getItems(): List<CartItem> = items.toList()

    fun registerListener(callback: () -> Unit) {
        listeners.add(callback)
    }

    fun unregisterListener(callback: () -> Unit) {
        listeners.remove(callback)
    }

    private fun notifyListeners() {
        listeners.forEach { try { it() } catch (_: Exception) {} }
    }

    fun getItemCount(): Int = items.sumOf { it.quantity }

    fun getProductCount(): Int = items.size

    fun subtotal(): Double = items.sumOf { (it.product.price * it.quantity).toDouble() }

    fun discountAmount(): Double = items.sumOf { ((it.product.originalPrice - it.product.price).coerceAtLeast(0.0) * it.quantity).toDouble() }

    fun total(): Double = (subtotal() - discountAmount())
}

package com.example.natraj

import android.content.Context
import android.content.SharedPreferences
import com.example.natraj.data.model.Product
import com.google.gson.Gson
import com.google.gson.reflect.TypeToken

object CartManager {
    private val items = mutableListOf<CartItem>()
    private val listeners = mutableListOf<() -> Unit>()
    private var sharedPrefs: SharedPreferences? = null
    private val gson = Gson()

    fun initialize(context: Context) {
        sharedPrefs = context.getSharedPreferences("cart_prefs", Context.MODE_PRIVATE)
        loadCartFromStorage()
    }

    private fun loadCartFromStorage() {
        sharedPrefs?.let { prefs ->
            val cartJson = prefs.getString("cart_items", "[]")
            try {
                val type = com.google.gson.reflect.TypeToken.getParameterized(java.util.List::class.java, CartItem::class.java).type
                val savedItems: List<CartItem> = gson.fromJson(cartJson, type) ?: emptyList()
                items.clear()
                items.addAll(savedItems)
            } catch (e: Exception) {
                items.clear() // Clear if parsing fails
            }
        }
    }

    private fun saveCartToStorage() {
        sharedPrefs?.let { prefs ->
            try {
                val cartJson = gson.toJson(items)
                prefs.edit().putString("cart_items", cartJson).apply()
            } catch (e: Exception) {
                // Handle save error silently
            }
        }
    }

    fun add(product: Product, qty: Int = 1) {
        val existing = items.find { it.product.id == product.id }
        if (existing != null) {
            existing.quantity += qty
        } else {
            items.add(CartItem(product, qty))
        }
        saveCartToStorage()
        notifyListeners()
    }

    fun remove(product: Product) {
        items.removeAll { it.product.id == product.id }
        saveCartToStorage()
        notifyListeners()
    }

    fun updateQuantity(product: Product, qty: Int) {
        val item = items.find { it.product.id == product.id }
        if (item != null) {
            if (qty <= 0) {
                remove(product)
            } else {
                item.quantity = qty
                saveCartToStorage()
                notifyListeners()
            }
        }
    }

    fun clear() {
        items.clear()
        saveCartToStorage()
        notifyListeners()
    }

    fun getItems(): List<CartItem> = items.toList()

    fun registerListener(callback: () -> Unit) {
        listeners.add(callback)
    }

    fun unregisterListener(callback: () -> Unit) {
        listeners.remove(callback)
    }

    private fun notifyListeners() {
        listeners.forEach { try { it() } catch (_: Exception) {} }
    }

    fun getItemCount(): Int = items.sumOf { it.quantity }

    fun getProductCount(): Int = items.size

    fun subtotal(): Double = items.sumOf { (it.product.price * it.quantity).toDouble() }

    fun discountAmount(): Double = items.sumOf { ((it.product.originalPrice - it.product.price).coerceAtLeast(0.0) * it.quantity).toDouble() }

    fun total(): Double = (subtotal() - discountAmount())

    fun hasProduct(productId: Int): Boolean = items.any { it.product.id == productId }

    fun getQuantity(productId: Int): Int = items.find { it.product.id == productId }?.quantity ?: 0
}

package com.example.natraj

import android.content.Context
import com.example.natraj.data.WpRepository
import kotlinx.coroutines.runBlocking

object OfferManager {
    private var offers: List<Offer> = emptyList()

    fun initialize(context: Context) {
        try {
            runBlocking {
                val wpRepository = WpRepository(context)
                val offerBanners = wpRepository.getOfferBanners()

                offers = if (offerBanners.isNotEmpty()) {
                    offerBanners.map { banner ->
                        Offer(
                            id = banner.id,
                            title = banner.title,
                            discount = extractDiscount(banner.subtitle),
                            originalPrice = "",
                            salePrice = "",
                            imageUrl = banner.imageUrl,
                            productUrl = "https://www.natrajsuper.com"
                        )
                    }
                } else {
                    // Strictly no fallback — empty list to allow UI to show Coming soon
                    emptyList()
                }
                android.util.Log.d("OfferManager", "Offers loaded: ${offers.size}")
            }
        } catch (e: Exception) {
            android.util.Log.e("OfferManager", "Error loading offers from WordPress", e)
            // Strictly no fallback
            offers = emptyList()
        }
    }

    private fun extractDiscount(subtitle: String): String {
        return when {
            subtitle.contains("50") -> "50%"
            subtitle.contains("40") -> "40%"
            subtitle.contains("30") -> "30%"
            subtitle.contains("29") -> "29%"
            subtitle.contains("26") -> "26%"
            subtitle.contains("23") -> "23%"
            subtitle.contains("52") -> "52%"
            subtitle.contains("47") -> "47%"
            subtitle.contains("33") -> "33%"
            else -> "UP TO 50%"
        }
    }

    fun getAllOffers(): List<Offer> = offers
}

