package com.example.natraj

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageView
import android.widget.TextView
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide

class BannerAdapter(
    private val banners: List<Banner>,
    private val onBannerClick: (Banner) -> Unit = {}
) : RecyclerView.Adapter<BannerAdapter.BannerViewHolder>() {

    inner class BannerViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        private val bannerImage: ImageView = itemView.findViewById(R.id.banner_image)
        private val bannerTitle: TextView = itemView.findViewById(R.id.banner_title)
        private val bannerSubtitle: TextView = itemView.findViewById(R.id.banner_subtitle)
        private val bannerDescription: TextView = itemView.findViewById(R.id.banner_description)

        fun bind(banner: Banner) {
            bannerTitle.text = banner.title
            bannerSubtitle.text = banner.subtitle
            bannerDescription.text = banner.description

            // Load banner image with better quality settings
            if (banner.imageUrl.isNotEmpty()) {
                Glide.with(itemView.context)
                    .load(banner.imageUrl)
                    .diskCacheStrategy(com.bumptech.glide.load.engine.DiskCacheStrategy.ALL)
                    .skipMemoryCache(false)
                    .placeholder(android.R.color.transparent)
                    .error(android.R.color.transparent)
                    .centerCrop()
                    .into(bannerImage)
            } else {
                // If no image URL, hide the image view to show gradient background
                bannerImage.setImageDrawable(null)
            }

            itemView.setOnClickListener { onBannerClick(banner) }
        }
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): BannerViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.item_banner, parent, false)
        return BannerViewHolder(view)
    }

    override fun onBindViewHolder(holder: BannerViewHolder, position: Int) {
        holder.bind(banners[position])
    }

    override fun getItemCount(): Int = banners.size
}

package com.example.natraj

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageView
import android.widget.TextView
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide

class BannerAdapter(
    private val banners: List<Banner>,
    private val onBannerClick: (Banner) -> Unit = {}
) : RecyclerView.Adapter<BannerAdapter.BannerViewHolder>() {

    inner class BannerViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        private val bannerImage: ImageView = itemView.findViewById(R.id.banner_image)
        private val bannerTitle: TextView = itemView.findViewById(R.id.banner_title)
        private val bannerSubtitle: TextView = itemView.findViewById(R.id.banner_subtitle)
        private val bannerDescription: TextView = itemView.findViewById(R.id.banner_description)

        fun bind(banner: Banner) {
            bannerTitle.text = banner.title
            bannerSubtitle.text = banner.subtitle
            bannerDescription.text = banner.description

            // Load banner image with better quality settings
            if (banner.imageUrl.isNotEmpty()) {
                Glide.with(itemView.context)
                    .load(banner.imageUrl)
                    .diskCacheStrategy(com.bumptech.glide.load.engine.DiskCacheStrategy.ALL)
                    .skipMemoryCache(false)
                    .placeholder(android.R.color.transparent)
                    .error(android.R.color.transparent)
                    .centerCrop()
                    .into(bannerImage)
            } else {
                // If no image URL, hide the image view to show gradient background
                bannerImage.setImageDrawable(null)
            }

            itemView.setOnClickListener { onBannerClick(banner) }
        }
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): BannerViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.item_banner, parent, false)

        // Ensure the page fills the whole ViewPager2 (fixes "Pages must fill the whole ViewPager2" crash)
        val lp = view.layoutParams
        if (lp == null) {
            view.layoutParams = RecyclerView.LayoutParams(ViewGroup.LayoutParams.MATCH_PARENT, ViewGroup.LayoutParams.MATCH_PARENT)
        } else {
            lp.width = ViewGroup.LayoutParams.MATCH_PARENT
            lp.height = ViewGroup.LayoutParams.MATCH_PARENT
            view.layoutParams = lp
        }

        return BannerViewHolder(view)
    }

    override fun onBindViewHolder(holder: BannerViewHolder, position: Int) {
        holder.bind(banners[position])
    }

    override fun getItemCount(): Int = banners.size
}

package com.example.natraj.ui.adapters

import android.content.Intent
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.Button
import android.widget.ImageView
import android.widget.TextView
import android.widget.Toast
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import com.example.natraj.CartManager
import com.example.natraj.R
import com.example.natraj.WishlistManager
import com.example.natraj.util.CustomToast
import com.example.natraj.data.model.Product

class GridProductAdapter(
    private var products: MutableList<Product> = mutableListOf(),
    private val onProductClick: (Product) -> Unit = {},
    private val onFavoriteClick: (Product) -> Unit = {},
    private val onAddToCart: (Product) -> Unit = {}
) : RecyclerView.Adapter<GridProductAdapter.GridProductViewHolder>() {

    inner class GridProductViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        val productImage: ImageView = itemView.findViewById(R.id.product_image)
        val productName: TextView = itemView.findViewById(R.id.product_name)
        val currentPrice: TextView = itemView.findViewById(R.id.current_price)
        val originalPrice: TextView = itemView.findViewById(R.id.original_price)
        val discountBadge: TextView = itemView.findViewById(R.id.discount_badge)
        val discountText: TextView = itemView.findViewById(R.id.discount_text)
        val ratingText: TextView = itemView.findViewById(R.id.rating_text)
        val ratingContainer: View = itemView.findViewById(R.id.rating_container)
        val stockStatus: TextView = itemView.findViewById(R.id.stock_status)
        val newBadge: TextView = itemView.findViewById(R.id.new_badge)
        val wishlistIcon: ImageView = itemView.findViewById(R.id.wishlist_icon)
        val addToCartButton: Button = itemView.findViewById(R.id.add_to_cart_button)
        val discountLayout: View = itemView.findViewById(R.id.discount_layout)

        fun bind(product: Product) {
            productName.text = product.name

            // Show transfer price if available, otherwise regular price
            val displayPrice = if (product.transferPrice > 0) product.transferPrice else product.price
            currentPrice.text = "₹${String.format("%,d", displayPrice.toInt())}"

            // Rating
            if (product.rating > 0) {
                ratingText.text = String.format("%.1f", product.rating)
                ratingContainer.visibility = View.VISIBLE
            } else {
                ratingContainer.visibility = View.GONE
            }

            // Stock status
            stockStatus.text = if (product.inventory > 0) "In stock" else "Out of stock"
            stockStatus.setTextColor(
                if (product.inventory > 0) 
                    itemView.context.getColor(R.color.stock_green)
                else
                    itemView.context.getColor(R.color.discount_badge_red)
            )

            // Handle MRP vs Transfer Price or Original Price
            val comparePrice = if (product.mrp > 0 && product.mrp > displayPrice) {
                product.mrp
            } else if (product.originalPrice > displayPrice) {
                product.originalPrice
            } else {
                0.0
            }

            if (comparePrice > displayPrice) {
                discountLayout.visibility = View.VISIBLE
                originalPrice.text = "₹${String.format("%,d", comparePrice.toInt())}"
                originalPrice.paintFlags = originalPrice.paintFlags or android.graphics.Paint.STRIKE_THRU_TEXT_FLAG

                val discount = ((comparePrice - displayPrice) / comparePrice * 100).toInt()
                discountBadge.visibility = View.VISIBLE
                discountBadge.text = "-${discount}%"
                discountText.text = "${discount}% OFF"
            } else {
                discountLayout.visibility = View.GONE
                discountBadge.visibility = View.GONE
            }

            // NEW badge - show for products added in last 30 days (if you have creation date)
            newBadge.visibility = View.GONE // Can be enabled based on product.createdDate

            // Wishlist state
            updateWishlistIcon(product.id)

            // Load product image with better quality and caching
            val imageToLoad = when {
                !product.images.isNullOrEmpty() -> product.images.first()
                !product.imageUrl.isNullOrEmpty() -> product.imageUrl
                else -> ""
            }
            val imageUri = if (imageToLoad.isNotEmpty() && !imageToLoad.startsWith("http")) {
                "file:///android_asset/$imageToLoad"
            } else imageToLoad

            Glide.with(itemView.context)
                .load(imageUri)
                .placeholder(R.color.background_light)
                .error(R.color.background_light)
                .fallback(R.color.background_light)
                .diskCacheStrategy(com.bumptech.glide.load.engine.DiskCacheStrategy.ALL)
                .centerCrop()
                .into(productImage)

            // Click listeners
            itemView.setOnClickListener {
                onProductClick(product)
            }

            wishlistIcon.setOnClickListener {
                val added = WishlistManager.toggle(product.id)
                updateWishlistIcon(product.id)

                val message = if (added) "Added to wishlist" else "Removed from wishlist"
                CustomToast.showInfo(itemView.context, message)
                onFavoriteClick(product)
            }

            // Add to Cart button
            addToCartButton.setOnClickListener {
                if (product.inventory > 0) {
                    CartManager.add(product, 1)

                    CustomToast.showSuccess(itemView.context, "Added to cart")
                    onAddToCart(product)
                } else {
                    CustomToast.showError(itemView.context, "Product out of stock")
                }
            }
        }

        private fun updateWishlistIcon(productId: Int) {
            if (WishlistManager.isInWishlist(productId)) {
                wishlistIcon.setColorFilter(android.graphics.Color.parseColor("#FF5722"))
            } else {
                wishlistIcon.setColorFilter(android.graphics.Color.parseColor("#878787"))
            }
        }

        // Removed external mock image fallbacks; rely on product image data or placeholders
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): GridProductViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.item_product_grid, parent, false)
        return GridProductViewHolder(view)
    }

    override fun onBindViewHolder(holder: GridProductViewHolder, position: Int) {
        holder.bind(products[position])
    }

    override fun getItemCount(): Int = products.size

    // Update entire product list (for initial load or filtering)
    fun update(newProducts: List<Product>) {
        products.clear()
        products.addAll(newProducts)
        notifyDataSetChanged()
    }

    // Append products for pagination
    fun append(newProducts: List<Product>) {
        val startPosition = products.size
        products.addAll(newProducts)
        notifyItemRangeInserted(startPosition, newProducts.size)
    }
}

package com.example.natraj.ui.adapters

import android.content.Intent
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.Button
import android.widget.ImageView
import android.widget.TextView
import android.widget.Toast
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide
import com.example.natraj.CartManager
import com.example.natraj.R
import com.example.natraj.WishlistManager
import com.example.natraj.util.CustomToast
import com.example.natraj.data.model.Product
import com.example.natraj.OrderManager

class GridProductAdapter(
    private var products: MutableList<Product> = mutableListOf(),
    private val onProductClick: (Product) -> Unit = {},
    private val onFavoriteClick: (Product) -> Unit = {},
    private val onAddToCart: (Product) -> Unit = {}
) : RecyclerView.Adapter<GridProductAdapter.GridProductViewHolder>() {

    inner class GridProductViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {
        val productImage: ImageView = itemView.findViewById(R.id.product_image)
        val productName: TextView = itemView.findViewById(R.id.product_name)
        val currentPrice: TextView = itemView.findViewById(R.id.current_price)
        val originalPrice: TextView = itemView.findViewById(R.id.original_price)
        val discountBadge: TextView = itemView.findViewById(R.id.discount_badge)
        val discountText: TextView = itemView.findViewById(R.id.discount_text)
        val ratingText: TextView = itemView.findViewById(R.id.rating_text)
        val ratingContainer: View = itemView.findViewById(R.id.rating_container)
        val stockStatus: TextView = itemView.findViewById(R.id.stock_status)
        val newBadge: TextView = itemView.findViewById(R.id.new_badge)
        val wishlistIcon: ImageView = itemView.findViewById(R.id.wishlist_icon)
        val addToCartButton: Button = itemView.findViewById(R.id.add_to_cart_button)
        val discountLayout: View = itemView.findViewById(R.id.discount_layout)

        // Newly added quantity controls
        val quantityControls: View = itemView.findViewById(R.id.quantity_controls)
        val decreaseBtn: ImageView = itemView.findViewById(R.id.decrease_quantity_btn)
        val increaseBtn: ImageView = itemView.findViewById(R.id.increase_quantity_btn)
        val quantityText: TextView = itemView.findViewById(R.id.quantity_text)

        fun bind(product: Product) {
            productName.text = product.name

            // Show transfer price if available, otherwise regular price
            val displayPrice = if (product.transferPrice > 0) product.transferPrice else product.price
            currentPrice.text = "₹${String.format("%,d", displayPrice.toInt())}"

            // Rating
            if (product.rating > 0) {
                ratingText.text = String.format("%.1f", product.rating)
                ratingContainer.visibility = View.VISIBLE
            } else {
                ratingContainer.visibility = View.GONE
            }

            // Stock status
            stockStatus.text = if (product.inventory > 0) "In stock" else "Out of stock"
            stockStatus.setTextColor(
                if (product.inventory > 0) 
                    itemView.context.getColor(R.color.stock_green)
                else
                    itemView.context.getColor(R.color.discount_badge_red)
            )

            // Handle MRP vs Transfer Price or Original Price
            val comparePrice = if (product.mrp > 0 && product.mrp > displayPrice) {
                product.mrp
            } else if (product.originalPrice > displayPrice) {
                product.originalPrice
            } else {
                0.0
            }

            if (comparePrice > displayPrice) {
                discountLayout.visibility = View.VISIBLE
                originalPrice.text = "₹${String.format("%,d", comparePrice.toInt())}"
                originalPrice.paintFlags = originalPrice.paintFlags or android.graphics.Paint.STRIKE_THRU_TEXT_FLAG

                val discount = ((comparePrice - displayPrice) / comparePrice * 100).toInt()
                discountBadge.visibility = View.VISIBLE
                discountBadge.text = "-${discount}%"
                discountText.text = "${discount}% OFF"
            } else {
                discountLayout.visibility = View.GONE
                discountBadge.visibility = View.GONE
            }

            // NEW badge - show for products added in last 30 days (if you have creation date)
            newBadge.visibility = View.GONE // Can be enabled based on product.createdDate

            // Wishlist state
            updateWishlistIcon(product.id)

            // Load product image with better quality and caching
            val imageToLoad = when {
                !product.images.isNullOrEmpty() -> product.images.first()
                !product.imageUrl.isNullOrEmpty() -> product.imageUrl
                else -> ""
            }
            val imageUri = if (imageToLoad.isNotEmpty() && !imageToLoad.startsWith("http")) {
                "file:///android_asset/$imageToLoad"
            } else imageToLoad

            Glide.with(itemView.context)
                .load(imageUri)
                .placeholder(R.color.background_light)
                .error(R.color.background_light)
                .fallback(R.color.background_light)
                .diskCacheStrategy(com.bumptech.glide.load.engine.DiskCacheStrategy.ALL)
                .centerCrop()
                .into(productImage)

            // Determine cart state
            val inCart = CartManager.hasProduct(product.id)
            val boughtBefore = OrderManager.hasPurchasedProduct(product.id)

            if (inCart) {
                // Show quantity controls
                addToCartButton.visibility = View.GONE
                quantityControls.visibility = View.VISIBLE
                val qty = CartManager.getQuantity(product.id)
                quantityText.text = qty.toString()
            } else {
                addToCartButton.visibility = View.VISIBLE
                quantityControls.visibility = View.GONE
                addToCartButton.text = if (boughtBefore) "BUY AGAIN" else "ADD"
            }

            // Add to Cart button
            addToCartButton.setOnClickListener {
                if (product.inventory > 0) {
                    CartManager.add(product, 1)

                    // Switch UI to quantity controls
                    addToCartButton.visibility = View.GONE
                    quantityControls.visibility = View.VISIBLE
                    quantityText.text = CartManager.getQuantity(product.id).toString()

                    CustomToast.showSuccess(itemView.context, "Added to cart")
                    onAddToCart(product)
                } else {
                    CustomToast.showError(itemView.context, "Product out of stock")
                }
            }

            // Increase / decrease logic
            increaseBtn.setOnClickListener {
                val current = CartManager.getQuantity(product.id)
                CartManager.updateQuantity(product, current + 1)
                quantityText.text = CartManager.getQuantity(product.id).toString()
            }

            decreaseBtn.setOnClickListener {
                val current = CartManager.getQuantity(product.id)
                if (current <= 1) {
                    // remove from cart
                    CartManager.remove(product)
                    quantityControls.visibility = View.GONE
                    addToCartButton.visibility = View.VISIBLE
                    addToCartButton.text = if (OrderManager.hasPurchasedProduct(product.id)) "BUY AGAIN" else "ADD"
                } else {
                    CartManager.updateQuantity(product, current - 1)
                    quantityText.text = CartManager.getQuantity(product.id).toString()
                }
            }

            // Click listeners
            itemView.setOnClickListener {
                onProductClick(product)
            }

            wishlistIcon.setOnClickListener {
                val added = WishlistManager.toggle(product.id)
                updateWishlistIcon(product.id)

                val message = if (added) "Added to wishlist" else "Removed from wishlist"
                CustomToast.showInfo(itemView.context, message)
                onFavoriteClick(product)
            }
        }

        private fun updateWishlistIcon(productId: Int) {
            if (WishlistManager.isInWishlist(productId)) {
                wishlistIcon.setColorFilter(android.graphics.Color.parseColor("#FF5722"))
            } else {
                wishlistIcon.setColorFilter(android.graphics.Color.parseColor("#878787"))
            }
        }

        // Removed external mock image fallbacks; rely on product image data or placeholders
    }

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): GridProductViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.item_product_grid, parent, false)
        return GridProductViewHolder(view)
    }

    override fun onBindViewHolder(holder: GridProductViewHolder, position: Int) {
        holder.bind(products[position])
    }

    override fun getItemCount(): Int = products.size

    // Update entire product list (for initial load or filtering)
    fun update(newProducts: List<Product>) {
        products.clear()
        products.addAll(newProducts)
        notifyDataSetChanged()
    }

    // Append products for pagination
    fun append(newProducts: List<Product>) {
        val startPosition = products.size
        products.addAll(newProducts)
        notifyItemRangeInserted(startPosition, newProducts.size)
    }
}

package com.example.natraj

import android.content.Intent
import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.Button
import android.widget.TextView
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import android.widget.Toast
import com.example.natraj.R
import com.example.natraj.ui.activities.MainActivity
import com.example.natraj.ui.fragments.HomeFragment

import com.google.android.material.bottomnavigation.BottomNavigationView

class CartFragment : Fragment() {

    private lateinit var recycler: RecyclerView
    private lateinit var adapter: CartAdapter
    private lateinit var itemCountText: TextView
    private lateinit var subtotalText: TextView
    private lateinit var discountText: TextView
    private lateinit var totalText: TextView
    private lateinit var checkoutBtn: Button
    private lateinit var emptyLayout: View
    private lateinit var summaryCard: View

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        val view = inflater.inflate(R.layout.fragment_cart, container, false)

        recycler = view.findViewById(R.id.cart_recycler)
        itemCountText = view.findViewById(R.id.cart_item_count)
        subtotalText = view.findViewById(R.id.subtotal_amount)
        discountText = view.findViewById(R.id.discount_amount)
        totalText = view.findViewById(R.id.total_amount)
        checkoutBtn = view.findViewById(R.id.checkout_btn)
        emptyLayout = view.findViewById(R.id.empty_cart_layout)
        summaryCard = view.findViewById(R.id.cart_summary_card)

        adapter = CartAdapter(CartManager.getItems(), { item, qty ->
            CartManager.updateQuantity(item.product, qty)
            refresh()
        }, { item ->
            CartManager.remove(item.product)
            refresh()
        })

        recycler.layoutManager = LinearLayoutManager(requireContext())
        recycler.adapter = adapter

        view.findViewById<Button>(R.id.continue_shopping_btn).setOnClickListener {
            // Add button press animation with ripple effect
            it.isEnabled = false // Prevent multiple clicks

            it.animate()
                .scaleX(0.95f)
                .scaleY(0.95f)
                .setDuration(100)
                .withEndAction {
                    it.animate()
                        .scaleX(1.0f)
                        .scaleY(1.0f)
                        .setDuration(100)
                        .withEndAction {
                            it.isEnabled = true // Re-enable button
                        }
                        .start()

                    // Navigate to home with smooth transition
                    try {
                        val mainActivity = activity as? MainActivity
                        mainActivity?.switchFragment(HomeFragment(), "Home")

                        // Update bottom navigation to show home is selected
                        mainActivity?.findViewById<BottomNavigationView>(R.id.bottom_navigation)
                            ?.selectedItemId = R.id.nav_home

                        // Show a brief success message
                        if (isAdded && context != null) {
                            val messages = arrayOf(
                                "Happy shopping! ",
                                "Let's find something amazing! ✨",
                                "Explore our latest arrivals! ",
                                "Your shopping adventure begins! "
                            )
                            val randomMessage = messages.random()
                            Toast.makeText(requireContext(), randomMessage, Toast.LENGTH_SHORT).show()
                        }
                    } catch (e: Exception) {
                        // Fallback navigation
                        parentFragmentManager.beginTransaction()
                            .setCustomAnimations(
                                android.R.anim.fade_in,
                                android.R.anim.fade_out,
                                android.R.anim.fade_in,
                                android.R.anim.fade_out
                            )
                            .replace(R.id.fragment_container, HomeFragment())
                            .commit()
                    }
                }
                .start()
        }

        checkoutBtn.setOnClickListener {
            if (CartManager.getItems().isEmpty()) {
                if (isAdded && context != null) {
                    Toast.makeText(requireContext(), "Cart is empty", Toast.LENGTH_SHORT).show()
                }
                return@setOnClickListener
            }

            // Check minimum order amount
            val items = CartManager.getItems()
            val subtotal = items.sumOf { (it.product.price * it.quantity).toDouble() }
            val MINIMUM_ORDER_AMOUNT = 700.0

            if (subtotal < MINIMUM_ORDER_AMOUNT) {
                if (isAdded && context != null) {
                    Toast.makeText(
                        requireContext(),
                        "Minimum order amount is ₹${MINIMUM_ORDER_AMOUNT.toInt()}. Current: ₹${subtotal.toInt()}",
                        Toast.LENGTH_LONG
                    ).show()
                }
                return@setOnClickListener
            }

            // Check login status before checkout
            if (!AuthManager.isLoggedIn()) {
                showLoginPromptForCheckout()
            } else {
                // Navigate to address page
                val intent = Intent(requireContext(), AddressActivity::class.java)
                startActivity(intent)
            }
        }

        refresh()
        return view
    }

    override fun onResume() {
        super.onResume()
        refresh()
    }

    private fun refresh() {
        try {
            val items = CartManager.getItems()
            adapter.update(items)

            if (items.isEmpty()) {
                emptyLayout.visibility = View.VISIBLE
                summaryCard.visibility = View.GONE
                recycler.visibility = View.GONE
            } else {
                emptyLayout.visibility = View.GONE
                summaryCard.visibility = View.VISIBLE
                recycler.visibility = View.VISIBLE

                val subtotal = items.sumOf { (it.product.price * it.quantity).toDouble() }
                val discount = subtotal * 0.05 // 5% discount
                val afterDiscount = subtotal - discount
                val delivery = if (afterDiscount > 50000) 0.0 else 500.0
                val total = afterDiscount + delivery

                val totalItems = items.sumOf { it.quantity.toDouble() }
                itemCountText.text = "Price ($totalItems items)"
                subtotalText.text = "₹${subtotal.toInt()}"
                discountText.text = "− ₹${discount.toInt()}"
                totalText.text = "₹${total.toInt()}"
            }
        } catch (e: Exception) {
            // Handle any errors gracefully
            emptyLayout.visibility = View.VISIBLE
            summaryCard.visibility = View.GONE
            recycler.visibility = View.GONE
        }
    }

    private fun showLoginPromptForCheckout() {
        androidx.appcompat.app.AlertDialog.Builder(requireContext())
            .setTitle("Login for Better Experience")
            .setMessage("Login to access saved addresses and track your orders. You can also continue as guest.")
            .setPositiveButton("Login") { _, _ ->
                val intent = Intent(requireContext(), LoginActivity::class.java)
                startActivity(intent)
            }
            .setNegativeButton("Continue as Guest") { _, _ ->
                // Allow guest checkout
                val intent = Intent(requireContext(), AddressActivity::class.java)
                startActivity(intent)
            }
            .setNeutralButton("Cancel", null)
            .show()
    }
}

package com.example.natraj

import android.app.Activity
import android.content.Intent
import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.Button
import android.widget.EditText
import android.widget.ImageView
import android.widget.TextView
import android.widget.Toast
import androidx.activity.result.contract.ActivityResultContracts
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.natraj.R
import com.example.natraj.ui.activities.MainActivity
import com.example.natraj.ui.fragments.HomeFragment
import com.google.android.material.bottomnavigation.BottomNavigationView
import android.speech.RecognizerIntent
import androidx.core.widget.addTextChangedListener

class CartFragment : Fragment() {

    private lateinit var recycler: RecyclerView
    private lateinit var adapter: CartAdapter
    private lateinit var itemCountText: TextView
    private lateinit var subtotalText: TextView
    private lateinit var discountText: TextView
    private lateinit var totalText: TextView
    private lateinit var checkoutBtn: Button
    private lateinit var emptyLayout: View
    private lateinit var summaryCard: View
    private lateinit var searchBar: EditText
    private lateinit var voiceIcon: ImageView

    // Keep a full snapshot of cart items for filtering
    private var fullCartSnapshot: List<CartItem> = emptyList()

    private val voiceSearchLauncher = registerForActivityResult(
        ActivityResultContracts.StartActivityForResult()
    ) { result ->
        if (result.resultCode == Activity.RESULT_OK) {
            val matches = result.data?.getStringArrayListExtra(RecognizerIntent.EXTRA_RESULTS)
            if (!matches.isNullOrEmpty()) {
                val searched = matches[0]
                searchBar.setText(searched)
                filterCartItems(searched)
            }
        }
    }

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        val view = inflater.inflate(R.layout.fragment_cart, container, false)

        recycler = view.findViewById(R.id.cart_recycler)
        itemCountText = view.findViewById(R.id.cart_item_count)
        subtotalText = view.findViewById(R.id.subtotal_amount)
        discountText = view.findViewById(R.id.discount_amount)
        totalText = view.findViewById(R.id.total_amount)
        checkoutBtn = view.findViewById(R.id.checkout_btn)
        emptyLayout = view.findViewById(R.id.empty_cart_layout)
        summaryCard = view.findViewById(R.id.cart_summary_card)

        searchBar = view.findViewById(R.id.search_bar)
        voiceIcon = view.findViewById(R.id.voice_search_icon_cart)

        adapter = CartAdapter(CartManager.getItems(), { item, qty ->
            CartManager.updateQuantity(item.product, qty)
            refresh()
        }, { item ->
            CartManager.remove(item.product)
            refresh()
        })

        recycler.layoutManager = LinearLayoutManager(requireContext())
        recycler.adapter = adapter

        // Keep a snapshot of full cart for filtering
        fullCartSnapshot = CartManager.getItems()

        // Search text changes: perform filter in real time
        searchBar.addTextChangedListener { text ->
            val q = text?.toString() ?: ""
            filterCartItems(q)
        }

        // Voice search
        voiceIcon.setOnClickListener {
            startVoiceSearch()
        }

        view.findViewById<Button>(R.id.continue_shopping_btn).setOnClickListener {
            // Add button press animation with ripple effect
            it.isEnabled = false // Prevent multiple clicks

            it.animate()
                .scaleX(0.95f)
                .scaleY(0.95f)
                .setDuration(100)
                .withEndAction {
                    it.animate()
                        .scaleX(1.0f)
                        .scaleY(1.0f)
                        .setDuration(100)
                        .withEndAction {
                            it.isEnabled = true // Re-enable button
                        }
                        .start()

                    // Navigate to home with smooth transition
                    try {
                        val mainActivity = activity as? MainActivity
                        mainActivity?.switchFragment(HomeFragment(), "Home")

                        // Update bottom navigation to show home is selected
                        mainActivity?.findViewById<BottomNavigationView>(R.id.bottom_navigation)
                            ?.selectedItemId = R.id.nav_home

                        // Show a brief success message
                        if (isAdded && context != null) {
                            val messages = arrayOf(
                                "Happy shopping! ",
                                "Let's find something amazing! ✨",
                                "Explore our latest arrivals! ",
                                "Your shopping adventure begins! "
                            )
                            val randomMessage = messages.random()
                            Toast.makeText(requireContext(), randomMessage, Toast.LENGTH_SHORT).show()
                        }
                    } catch (e: Exception) {
                        // Fallback navigation
                        parentFragmentManager.beginTransaction()
                            .setCustomAnimations(
                                android.R.anim.fade_in,
                                android.R.anim.fade_out,
                                android.R.anim.fade_in,
                                android.R.anim.fade_out
                            )
                            .replace(R.id.fragment_container, HomeFragment())
                            .commit()
                    }
                }
                .start()
        }

        checkoutBtn.setOnClickListener {
            if (CartManager.getItems().isEmpty()) {
                if (isAdded && context != null) {
                    Toast.makeText(requireContext(), "Cart is empty", Toast.LENGTH_SHORT).show()
                }
                return@setOnClickListener
            }

            // Check minimum order amount
            val items = CartManager.getItems()
            val subtotal = items.sumOf { (it.product.price * it.quantity).toDouble() }
            val MINIMUM_ORDER_AMOUNT = 700.0

            if (subtotal < MINIMUM_ORDER_AMOUNT) {
                if (isAdded && context != null) {
                    Toast.makeText(
                        requireContext(),
                        "Minimum order amount is ₹${MINIMUM_ORDER_AMOUNT.toInt()}. Current: ₹${subtotal.toInt()}",
                        Toast.LENGTH_LONG
                    ).show()
                }
                return@setOnClickListener
            }

            // Check login status before checkout
            if (!AuthManager.isLoggedIn()) {
                showLoginPromptForCheckout()
            } else {
                // Navigate to address page
                val intent = Intent(requireContext(), AddressActivity::class.java)
                startActivity(intent)
            }
        }

        refresh()
        return view
    }

    private fun startVoiceSearch() {
        try {
            val intent = Intent(RecognizerIntent.ACTION_RECOGNIZE_SPEECH).apply {
                putExtra(RecognizerIntent.EXTRA_LANGUAGE_MODEL, RecognizerIntent.LANGUAGE_MODEL_FREE_FORM)
                putExtra(RecognizerIntent.EXTRA_LANGUAGE, java.util.Locale.getDefault())
                putExtra(RecognizerIntent.EXTRA_PROMPT, "Say item name to search in cart...")
            }
            voiceSearchLauncher.launch(intent)
        } catch (e: Exception) {
            Toast.makeText(requireContext(), "Voice search not available", Toast.LENGTH_SHORT).show()
        }
    }

    private fun filterCartItems(query: String) {
        try {
            val trimmed = query.trim()
            if (trimmed.isEmpty()) {
                // restore full
                val list = CartManager.getItems()
                adapter.update(list)
                fullCartSnapshot = list
                refresh()
                return
            }

            val lower = trimmed.lowercase()
            val filtered = fullCartSnapshot.filter { ci ->
                val name = ci.product.name ?: ""
                name.lowercase().contains(lower) || ci.product.articleCode?.lowercase()?.contains(lower) == true
            }

            adapter.update(filtered)
            // update summary area for filtered view — keep totals of filtered items
            if (filtered.isEmpty()) {
                emptyLayout.visibility = View.VISIBLE
                summaryCard.visibility = View.GONE
                recycler.visibility = View.GONE
            } else {
                emptyLayout.visibility = View.GONE
                summaryCard.visibility = View.VISIBLE
                recycler.visibility = View.VISIBLE

                val subtotal = filtered.sumOf { (it.product.price * it.quantity).toDouble() }
                val discount = subtotal * 0.05
                val after = subtotal - discount
                val delivery = if (after > 50000) 0.0 else 500.0
                val total = after + delivery

                val totalItems = filtered.sumOf { it.quantity.toDouble() }
                itemCountText.text = "Price ($totalItems items)"
                subtotalText.text = "₹${subtotal.toInt()}"
                discountText.text = "− ₹${discount.toInt()}"
                totalText.text = "₹${total.toInt()}"
            }
        } catch (e: Exception) {
            // fallback to full list
            adapter.update(fullCartSnapshot)
            refresh()
        }
    }

    override fun onResume() {
        super.onResume()
        // refresh snapshot when returning
        fullCartSnapshot = CartManager.getItems()
        refresh()
    }

    private fun refresh() {
        try {
            val items = CartManager.getItems()
            adapter.update(items)
            fullCartSnapshot = items

            if (items.isEmpty()) {
                emptyLayout.visibility = View.VISIBLE
                summaryCard.visibility = View.GONE
                recycler.visibility = View.GONE
            } else {
                emptyLayout.visibility = View.GONE
                summaryCard.visibility = View.VISIBLE
                recycler.visibility = View.VISIBLE

                val subtotal = items.sumOf { (it.product.price * it.quantity).toDouble() }
                val discount = subtotal * 0.05 // 5% discount
                val afterDiscount = subtotal - discount
                val delivery = if (afterDiscount > 50000) 0.0 else 500.0
                val total = afterDiscount + delivery

                val totalItems = items.sumOf { it.quantity.toDouble() }
                itemCountText.text = "Price ($totalItems items)"
                subtotalText.text = "₹${subtotal.toInt()}"
                discountText.text = "− ₹${discount.toInt()}"
                totalText.text = "₹${total.toInt()}"
            }
        } catch (e: Exception) {
            // Handle any errors gracefully
            emptyLayout.visibility = View.VISIBLE
            summaryCard.visibility = View.GONE
            recycler.visibility = View.GONE
        }
    }

    private fun showLoginPromptForCheckout() {
        androidx.appcompat.app.AlertDialog.Builder(requireContext())
            .setTitle("Login for Better Experience")
            .setMessage("Login to access saved addresses and track your orders. You can also continue as guest.")
            .setPositiveButton("Login") { _, _ ->
                val intent = Intent(requireContext(), LoginActivity::class.java)
                startActivity(intent)
            }
            .setNegativeButton("Continue as Guest") { _, _ ->
                // Allow guest checkout
                val intent = Intent(requireContext(), AddressActivity::class.java)
                startActivity(intent)
            }
            .setNeutralButton("Cancel", null)
            .show()
    }
}

